import jsPDF from 'jspdf';
import 'jspdf-autotable';

// Embedded PNG logo (ANDRITZ + Clearwater Paper)
// Stored as a data URL so jsPDF can render it without async loading.
const BRAND_LOGO_PNG =
  'data:image/png;base64,';

// ─── ANDRITZ Brand Colors ──────────────────────────────────────────────────────
const BLUE = [0, 117, 191];
const DARK_BLUE = [0, 58, 112];
const RED = [228, 0, 43];
const GREEN_LEAF = [76, 175, 80];
const DK_GREEN = [46, 125, 50];
const WHITE = [255, 255, 255];
const DARK_TEXT = [40, 50, 65];
const BODY_TEXT = [70, 85, 105];
const MUTED = [120, 135, 150];
const BORDER_LT = [210, 225, 235];
const BORDER_MID = [185, 205, 220];
const ROW_BG = [238, 246, 252];
const WARN_BG = [255, 248, 225];
const ALERT_BG = [253, 236, 234];
const OK_BG = [232, 245, 233];

// ─── Page Layout ─────────────────────────────────────────────────────────────
const PW = 210;
const PH = 297;
const ML = 12;
const MR = 12;
const CW = PW - ML - MR;
const CX = ML;
const CY = 40;
const FONT = 'helvetica';

// ─── Helpers ─────────────────────────────────────────────────────────────────
function tx(doc, size, weight, color, text, x, y, opts = {}) {
  doc.setFont(FONT, weight);
  doc.setFontSize(size);
  doc.setTextColor(...color);
  doc.text(String(text ?? ''), x, y, opts);
}
function rr(doc, x, y, w, h, r = 3, fill = null, stroke = null, lw = 0.4) {
  if (fill) doc.setFillColor(...fill);
  if (stroke) doc.setDrawColor(...stroke);
  doc.setLineWidth(lw);
  doc.roundedRect(x, y, w, h, r, r, fill ? 'FD' : 'S');
}
function dot(doc, x, y, r = 1.2, fill = BLUE) {
  doc.setFillColor(...fill);
  doc.circle(x, y, r, 'F');
}
function bg(doc) {
  doc.setFillColor(...WHITE);
  doc.rect(0, 0, PW, PH, 'F');
}
function pageFooter(doc, id) {
  doc.setDrawColor(...BORDER_MID);
  doc.setLineWidth(0.4);
  doc.line(ML, PH - 13, PW - MR, PH - 13);
  tx(doc, 8, 'normal', MUTED, `Document ID: ${id}`, ML, PH - 7);
  tx(doc, 8, 'normal', MUTED, `Page ${doc.internal.getNumberOfPages()}`, PW - MR, PH - 7, {
    align: 'right'
  });
}

function drawLogo(doc, millName) {
  // Logo box with white bg
  rr(doc, ML, 3, 58, 33, 4, WHITE, BORDER_MID, 0.6);

  // Draw the real PNG logo inside the box
  try {
    const pad = 2.5;
    const x = ML + pad;
    const y = 3 + pad;
    const w = 58 - pad * 2;
    const h = 33 - pad * 2;

    doc.addImage(BRAND_LOGO_PNG, 'PNG', x, y, w, h, undefined, 'FAST');

    // Optional: show mill/site name in small text (kept subtle)
    if (millName && String(millName).trim()) {
      const label = String(millName).toUpperCase();
      tx(
        doc,
        5.5,
        'bold',
        DARK_TEXT,
        label.length > 24 ? label.slice(0, 23) + '…' : label,
        ML + 4,
        36.5
      );
    }
  } catch (e) {
    // Fallback to vector-style mark if image fails
    tx(doc, 16, 'bold', DARK_BLUE, 'ANDRiTZ', ML + 4, 15);

    const lx = ML + 4,
      ly = 22;
    doc.setFillColor(...GREEN_LEAF);
    doc.triangle(lx, ly + 5, lx + 8, ly, lx + 14, ly, 'F');
    doc.setFillColor(...DK_GREEN);
    doc.triangle(lx + 2, ly + 8, lx + 10, ly + 3, lx + 16, ly + 3, 'F');

    tx(doc, 6, 'bold', DARK_TEXT, String(millName || '').toUpperCase(), ML + 20, ly + 4);
    tx(doc, 5.5, 'normal', MUTED, 'PAPER', ML + 20, ly + 9);
  }
}

function header(doc, title, subtitle, millName) {
  // White header bg
  doc.setFillColor(...WHITE);
  doc.rect(0, 0, PW, 40, 'F');
  doc.setDrawColor(...BORDER_MID);
  doc.setLineWidth(0.5);
  doc.line(0, 40, PW, 40);

  drawLogo(doc, millName);

  // Vertical divider
  doc.setDrawColor(...BORDER_MID);
  doc.setLineWidth(0.8);
  doc.line(ML + 61, 5, ML + 61, 37);

  // Title block
  tx(doc, 20, 'bold', DARK_BLUE, title, ML + 65, 18);
  tx(doc, 11, 'bold', BLUE, subtitle, ML + 65, 29);
}

function keyValRow(doc, x, y, w, label, value, {
  labelW = 30,
  rowH = 10,
  bg = ROW_BG
} = {}) {
  rr(doc, x, y, w, rowH, 3, bg, BORDER_LT);
  tx(doc, 7.5, 'bold', MUTED, label, x + 4, y + 6.8);
  doc.setDrawColor(...BORDER_MID);
  doc.setLineWidth(0.4);
  doc.line(x + labelW, y + 2, x + labelW, y + rowH - 2);
  tx(doc, 9, 'bold', DARK_TEXT, value || '', x + labelW + 4, y + 6.9);
}

function multilineBox(doc, x, y, w, h, titleText, bodyText, {
  titleBg = ROW_BG,
  bodyBg = WHITE
} = {}) {
  rr(doc, x, y, w, h, 4, bodyBg, BORDER_LT);
  rr(doc, x, y, w, 10, 4, titleBg, BORDER_LT);
  tx(doc, 8, 'bold', BLUE, titleText, x + 4, y + 7);
  doc.setTextColor(...BODY_TEXT);
  doc.setFont(FONT, 'normal');
  doc.setFontSize(9);

  const wrapped = doc.splitTextToSize(String(bodyText || ''), w - 8);
  doc.text(wrapped, x + 4, y + 16);
}

function statusPill(doc, x, y, label) {
  const s = String(label || '').toUpperCase();
  const map = {
    acceptable: { bg: OK_BG, border: DK_GREEN, text: DK_GREEN },
    caution: { bg: WARN_BG, border: [184, 134, 11], text: [184, 134, 11] },
    alert: { bg: ALERT_BG, border: RED, text: RED }
  };
  const k = s.includes('ACCEPT') ? 'acceptable' : s.includes('CAUT') ? 'caution' : 'alert';
  const style = map[k];
  rr(doc, x, y, 30, 9, 5, style.bg, style.border, 0.6);
  tx(doc, 7.5, 'bold', style.text, style.bg === OK_BG ? 'ACCEPTABLE' : style.bg === WARN_BG ? 'CAUTION' : 'ALERT', x + 15, y + 6.3, {
    align: 'center'
  });
}

// ─── Report 1: Condition Monitoring Report ────────────────────────────────────
export function generateConditionMonitorPDF(data, millName) {
  const doc = new jsPDF({ unit: 'mm', format: 'a4' });
  const id = `CMR-${String(Math.floor(Math.random() * 999)).padStart(3, '0')}-${new Date().getFullYear()}`;

  bg(doc);
  header(doc, 'Condition Monitoring Report', 'Vibration Diagnosis and Recommendation', millName);

  // Body region
  let y = CY + 10;

  // Top row: Date, WO, Technician
  const colW = (CW - 8) / 3;
  keyValRow(doc, CX, y, colW, 'Date', data.date || '');
  keyValRow(doc, CX + colW + 4, y, colW, 'Work Order', data.woNumber || '');
  keyValRow(doc, CX + (colW + 4) * 2, y, colW, 'Technician', data.technicianName || '');
  y += 14;

  // Asset info
  keyValRow(doc, CX, y, CW, 'Asset Name', data.assetName || '', { labelW: 35 });
  y += 12;

  keyValRow(doc, CX, y, (CW - 4) / 2, 'Asset ID', data.assetId || '', { labelW: 28 });
  keyValRow(doc, CX + (CW + 4) / 2, y, (CW - 4) / 2, 'Location', data.area || '', { labelW: 30 });
  y += 14;

  // Status + levels row
  const half = (CW - 4) / 2;
  rr(doc, CX, y, half, 22, 4, WHITE, BORDER_LT);
  rr(doc, CX + half + 4, y, half, 22, 4, WHITE, BORDER_LT);

  tx(doc, 8, 'bold', BLUE, 'Status Condition', CX + 4, y + 7);
  statusPill(doc, CX + 4, y + 10, data.statusCondition || 'Alert');

  tx(doc, 8, 'bold', BLUE, 'Vibration Level (G)', CX + half + 8, y + 7);
  const vibBg = (String(data.statusCondition || '').toLowerCase().includes('accept') ? OK_BG :
    String(data.statusCondition || '').toLowerCase().includes('caut') ? WARN_BG : ALERT_BG);
  rr(doc, CX + half + 8, y + 10, half - 12, 9, 4, vibBg, BORDER_MID);
  tx(doc, 10, 'bold', DARK_TEXT, data.vibrationLevel || '', CX + half + 8 + (half - 12) / 2, y + 16.3, { align: 'center' });
  y += 28;

  // Diagnosis row
  keyValRow(doc, CX, y, CW, 'Vibration Diagnosis', data.diagnosis || '', { labelW: 45 });
  y += 14;

  // Evidence images section
  tx(doc, 11, 'bold', DARK_BLUE, 'Evidence', CX, y);
  y += 6;

  const imgW = (CW - 8) / 3;
  const imgH = 38;

  function imageBox(ix, label, imgData) {
    const x = CX + ix * (imgW + 4);
    rr(doc, x, y, imgW, imgH, 4, WHITE, BORDER_LT);
    rr(doc, x, y, imgW, 9, 4, ROW_BG, BORDER_LT);
    tx(doc, 7.5, 'bold', MUTED, label, x + 3, y + 6.3);

    if (imgData) {
      try {
        doc.addImage(imgData, 'JPEG', x + 3, y + 11, imgW - 6, imgH - 14, undefined, 'FAST');
      } catch (e) {
        try {
          doc.addImage(imgData, 'PNG', x + 3, y + 11, imgW - 6, imgH - 14, undefined, 'FAST');
        } catch (e2) {
          tx(doc, 8, 'normal', MUTED, 'Invalid image', x + imgW / 2, y + imgH / 2, { align: 'center' });
        }
      }
    } else {
      tx(doc, 8, 'normal', MUTED, 'No attachment', x + imgW / 2, y + imgH / 2, { align: 'center' });
    }
  }

  imageBox(0, 'Trend Chart', data.trendImage);
  imageBox(1, 'Spectrum', data.spectrumImage);
  imageBox(2, 'Machine Photo', data.machineImage);

  y += imgH + 10;

  // Recommendations boxes
  multilineBox(doc, CX, y, CW, 45, 'Description', data.observations || '');
  y += 50;
  multilineBox(doc, CX, y, CW, 55, 'Recommendation', data.recommendation || '', { titleBg: ALERT_BG });
  y += 60;

  pageFooter(doc, id);
  doc.save(`${id}_Condition_Monitor.pdf`);
  return id;
}

// ─── Report 2: Post Maintenance Form ──────────────────────────────────────────
export function generatePostMaintenancePDF(data, millName) {
  const doc = new jsPDF({ unit: 'mm', format: 'a4' });
  const id = `PMF-${String(Math.floor(Math.random() * 999)).padStart(3, '0')}-${new Date().getFullYear()}`;

  bg(doc);
  header(doc, 'Post Maintenance Form', 'Maintenance Record and Verification', millName);

  let y = CY + 10;

  const colW = (CW - 8) / 3;
  keyValRow(doc, CX, y, colW, 'Date', data.maintenanceDate || '');
  keyValRow(doc, CX + colW + 4, y, colW, 'Work Order', data.woNumber || '');
  keyValRow(doc, CX + (colW + 4) * 2, y, colW, 'Technician', data.technicianName || '');
  y += 14;

  keyValRow(doc, CX, y, CW, 'Asset Name', data.assetName || '', { labelW: 35 });
  y += 12;

  keyValRow(doc, CX, y, (CW - 4) / 2, 'Asset ID', data.assetId || '', { labelW: 28 });
  keyValRow(doc, CX + (CW + 4) / 2, y, (CW - 4) / 2, 'Location', data.area || '', { labelW: 30 });
  y += 16;

  tx(doc, 11, 'bold', DARK_BLUE, 'Pre / Post Readings', CX, y);
  y += 6;

  const half = (CW - 4) / 2;
  rr(doc, CX, y, half, 34, 4, WHITE, BORDER_LT);
  rr(doc, CX + half + 4, y, half, 34, 4, WHITE, BORDER_LT);

  tx(doc, 8, 'bold', BLUE, 'Before', CX + 4, y + 7);
  keyValRow(doc, CX + 4, y + 10, half - 8, 'Vibration (in/s)', data.vibrationBefore || '', { labelW: 45, rowH: 10 });
  keyValRow(doc, CX + 4, y + 21, half - 8, 'Temperature (F)', data.tempBefore || '', { labelW: 45, rowH: 10 });

  tx(doc, 8, 'bold', BLUE, 'After', CX + half + 8, y + 7);
  keyValRow(doc, CX + half + 8, y + 10, half - 12, 'Vibration (in/s)', data.vibrationAfter || '', { labelW: 45, rowH: 10 });
  keyValRow(doc, CX + half + 8, y + 21, half - 12, 'Temperature (F)', data.tempAfter || '', { labelW: 45, rowH: 10 });

  y += 44;

  multilineBox(doc, CX, y, CW, 55, 'Maintenance Performed', data.maintenancePerformed || '', { titleBg: ROW_BG });
  y += 60;

  multilineBox(doc, CX, y, CW, 40, 'Parts Replaced', data.partsReplaced || '', { titleBg: ROW_BG });
  y += 45;

  multilineBox(doc, CX, y, CW, 40, 'Notes', data.notes || '', { titleBg: ROW_BG });

  pageFooter(doc, id);
  doc.save(`${id}_Post_Maintenance.pdf`);
  return id;
}

// ─── Report 3: RCFA Form ──────────────────────────────────────────────────────
export function generateRCFAPDF(data, millName) {
  const doc = new jsPDF({ unit: 'mm', format: 'a4' });
  const id = `RCFA-${String(Math.floor(Math.random() * 999)).padStart(3, '0')}-${new Date().getFullYear()}`;

  bg(doc);
  header(doc, 'RCFA Form', 'Root Cause Failure Analysis', millName);

  let y = CY + 10;

  const colW = (CW - 8) / 3;
  keyValRow(doc, CX, y, colW, 'Event Date', data.eventDate || '');
  keyValRow(doc, CX + colW + 4, y, colW, 'Work Order', data.woNumber || '');
  keyValRow(doc, CX + (colW + 4) * 2, y, colW, 'Analyst', data.analystName || '');
  y += 14;

  keyValRow(doc, CX, y, (CW - 4) / 2, 'Location', data.area || '', { labelW: 30 });
  keyValRow(doc, CX + (CW + 4) / 2, y, (CW - 4) / 2, 'Failed Component', data.failedComponent || '', { labelW: 40 });
  y += 14;

  multilineBox(doc, CX, y, CW, 35, 'Problem Description', data.problemDescription || '', { titleBg: ROW_BG });
  y += 40;

  tx(doc, 11, 'bold', DARK_BLUE, '5 Whys', CX, y);
  y += 6;

  const rowH = 10;
  for (let i = 1; i <= 5; i++) {
    keyValRow(doc, CX, y, CW, `Why ${i}`, data[`why${i}`] || '', { labelW: 25, rowH });
    y += 11;
  }

  y += 4;

  multilineBox(doc, CX, y, CW, 35, 'Root Cause', data.rootCause || '', { titleBg: ALERT_BG });
  y += 40;

  multilineBox(doc, CX, y, CW, 45, 'Corrective Actions', data.correctiveActions || '', { titleBg: OK_BG });

  pageFooter(doc, id);
  doc.save(`${id}_RCFA.pdf`);
  return id;
}

// ─── Report 4: Safety Form (JSA style) ────────────────────────────────────────
export function generateSafetyPDF(data, millName) {
  const doc = new jsPDF({ unit: 'mm', format: 'a4' });
  const id = `JSA-${String(Math.floor(Math.random() * 999)).padStart(3, '0')}-${new Date().getFullYear()}`;

  bg(doc);
  header(doc, 'Safety Form', 'Job Safety Analysis', millName);

  let y = CY + 10;

  const colW = (CW - 8) / 3;
  keyValRow(doc, CX, y, colW, 'Date', data.date || '');
  keyValRow(doc, CX + colW + 4, y, colW, 'Location', data.area || '');
  keyValRow(doc, CX + (colW + 4) * 2, y, colW, 'Employee', data.employeeName || '');
  y += 14;

  multilineBox(doc, CX, y, CW, 30, 'Task Description', data.taskDescription || '', { titleBg: ROW_BG });
  y += 35;

  tx(doc, 11, 'bold', DARK_BLUE, 'PPE Checklist', CX, y);
  y += 6;

  const ppe = [
    ['Hard Hat', data.hardHat],
    ['Safety Glasses', data.safetyGlasses],
    ['Steel Toe Boots', data.steelToeBoots],
    ['High Vis Vest', data.highVisVest],
    ['Hearing Protection', data.hearingProtection],
    ['Gloves', data.gloves],
    ['Fall Protection', data.fallProtection],
    ['Face Shield', data.faceShield]
  ];

  const boxW = (CW - 8) / 2;
  const boxH = 8.5;
  for (let i = 0; i < ppe.length; i++) {
    const col = i % 2;
    const row = Math.floor(i / 2);
    const x = CX + col * (boxW + 4);
    const yy = y + row * (boxH + 3);
    rr(doc, x, yy, boxW, boxH, 3, WHITE, BORDER_LT);
    const checked = !!ppe[i][1];
    rr(doc, x + 3, yy + 2.2, 4.8, 4.8, 1, checked ? OK_BG : WHITE, BORDER_MID);
    if (checked) {
      tx(doc, 10, 'bold', DK_GREEN, '✓', x + 5.4, yy + 6.2, { align: 'center' });
    }
    tx(doc, 8.5, 'bold', MUTED, ppe[i][0], x + 10, yy + 6.2);
  }

  y += 4 * (boxH + 3) + 8;

  multilineBox(doc, CX, y, CW, 45, 'Hazards Identified', data.hazards || '', { titleBg: WARN_BG });
  y += 50;

  multilineBox(doc, CX, y, CW, 55, 'Control Measures', data.controlMeasures || '', { titleBg: OK_BG });
  y += 60;

  // Signatures
  tx(doc, 11, 'bold', DARK_BLUE, 'Signatures', CX, y);
  y += 6;

  const sigH = 20;
  const hw = (CW - 4) / 2;
  const GAP = 4;

  rr(doc, CX, y, hw, sigH, 4, ROW_BG, BORDER_LT);
  dot(doc, CX + 5.5, y + 6.5);
  tx(doc, 7.5, 'bold', MUTED, 'Employee Signature', CX + 10, y + 7);
  doc.setDrawColor(...BORDER_MID);
  doc.setLineWidth(0.4);
  doc.line(CX + 5, y + sigH - 5, CX + hw - 5, y + sigH - 5);
  tx(doc, 9, 'bold', BLUE, data.employeeName || '', CX + 6, y + sigH - 6.5);

  rr(doc, CX + hw + GAP, y, hw, sigH, 4, ROW_BG, BORDER_LT);
  dot(doc, CX + hw + GAP + 5.5, y + 6.5);
  tx(doc, 7.5, 'bold', MUTED, 'Supervisor Signature', CX + hw + GAP + 10, y + 7);
  doc.setDrawColor(...BORDER_MID);
  doc.setLineWidth(0.4);
  doc.line(CX + hw + GAP + 5, y + sigH - 5, CX + CW - 5, y + sigH - 5);
  tx(doc, 9, 'bold', BLUE, data.supervisor || '', CX + hw + GAP + 6, y + sigH - 6.5);

  pageFooter(doc, id);
  doc.save(`${id}_Safety_Form.pdf`);
  return id;
}